import React from 'react';
import '../App.css';
import { motion } from "framer-motion";
import {about} from "./constants";
import styles, {layout} from "../style";

const AboutMe = () => {
  const aboutItem = about.map(info =>
    <AboutDisplay info ={info} />
  )

  return (
    <div>
      <h2 className={`${styles.heading2} flex flex-1`}>About Me</h2>
      {aboutItem}
    </div>
  )
}

const AboutDisplay = () => {
  return (
    <section id="about" className={`${layout.section} md:py-32 py-16`}>
      <div className={`${layout.sectionImg}`}>
        <img className="rounded-br-full" src={about.src} alt="NA" />
      </div>
      <div className={`${layout.sectionInfo} max-w-[500px]`}>
        <p className={styles.paragraph}>{about.info}</p>
      </div>
    </section>
  )
}

export default AboutMe
