import React from 'react';
import { useState } from 'react';
import { motion } from "framer-motion";
import {projects} from "./constants";

const ProjectTable = () => {
  const projectItem = projects.map(project =>
    <ProjectDisplay project={project} />
  )

  return (
    <section id={"projects"} className="">
      <h1 className={"ss:text-[52px] text-[32px] font-medium text-white"}>Projects</h1>
      <div className="projects__container">
        <div className={"flex z-[5]"}>
          <ul className="flex-1">
            {projectItem}
          </ul>
        </div>

        <div className="absolute z-[3] -left-1/2 top-0 w-[50%]
          rounded-full  white__gradient" />
        <div />
      </div>
    </section>
  );
}

const ProjectDisplay = ({project}) => {
  const [show, setShow] = useState(false);
  const onClick = () => setShow(!show)

  return (
    <li className="projects__item">
      <figure className="projects__item__pic-wrap">
        <img className="rounded-xl w-[400px] shadow relative"
             src={project.img} alt={project.name}
            onClick={onClick} />
        {show ? <ShowProject project={project} /> : null}
      </figure>
    </li>
  )
}

const ShowProject = ({project}) =>{
  return (
    <div>
      <h5>{project.name}</h5>
      <p>{project.desc}</p>
    </div>
  )
}


export default ProjectTable
