import React from 'react';
import { useState } from 'react';
import { motion } from "framer-motion";
import {projects} from "./constants";

const ProjectTable = () => {
  const projectItem = projects.map(project =>
    <ProjectDisplay project={project} />
  )

  return (
    <div className="">
      <h1 className={"text-xxl font-medium text-white"}>Projects</h1>
      <div className="projects__container">
        <div className={"flex"}>
          <ul className="flex-1">
            {projectItem}
          </ul>
        </div>
      </div>
    </div>
  );
}

const ProjectDisplay = ({project}) => {
  const [show, setShow] = useState(false);
  const onClick = () => setShow(!show)

  return (
    <li className="projects__item">
      <figure className="projects__item__pic-wrap">
        <img className="rounded-xl w-[500px] shadow"
             src={project.img} alt={project.name}
            onClick={onClick} />
        {show ? <ShowProject project={project} /> : null}
      </figure>
    </li>
  )
}

const ShowProject = ({project}) =>{
  return (
    <div>
      <h5>{project.name}</h5>
      <p>{project.desc}</p>
    </div>
  )
}


export default ProjectTable
