import React from 'react';
import '../App.css';
import { useState } from 'react';
import { motion } from "framer-motion";

function ProjectTable() {
  const projectItem = projects.map(project =>
    <ProjectDisplay project={project} />
  )

  return (
    <div className="ProjectTable">
      <h1>Projects</h1>
      {projectItem}
    </div>
  );
}

function ProjectDisplay({project}) {
  const [show, setShow] = useState(false);
  const onClick = () => setShow(!show)

  return (
    <div className="ProjectDisplay">
      <img src={project.img} alt={project.name}
        onClick={onClick}></img>
      {show ? <ShowProject project={project} /> : null}
    </div>
  )
}

const ShowProject = ({project}) =>{
  return (
    <div>
      <h5>{project.name}</h5>
      <p>{project.desc}</p>
    </div>
  )
}

function TitleCard(props) {
  return (
    <div className="TitleCard">
    </div>
  )
}

const projects = [
  {name: "Stat Tracker",
    desc: "Application made in python to add statistic information and show them on graphics.",
    img: "img/stat_tracker.jpg"},
  {name: "Space Pew",
    desc: "Pygame application with score progression, lives, and power ups.",
    img: "img/space_pew.jpg"}
]

export default ProjectTable
