import React from 'react';
import { useState } from 'react';
import { motion } from "framer-motion";
import {projects} from "./constants";
import styles, { layout } from "../style";

const ProjectTable = () => {
  const projectItem = projects.map(project =>
    <ProjectDisplay project={project} />
  )

  return (
    <section id={"projects"}>
      <h2 className={styles.heading2}>Projects</h2>
      <div className="projects__container">
        <div>
          <ul>
            {projectItem}
          </ul>

          <div className="absolute z-[3] -left-1/2 top-0 w-[50%] h-[50%]
          rounded-full  white__gradient" />
          <div className="absolute z-[0] -left-1/2 top-0 w-[50%] h-[50%]
          rounded-full  blue__gradient" />
        </div>

        <div />
      </div>
    </section>
  );
}

const ProjectDisplay = ({project}) => {
  const [show, setShow] = useState(false);
  const onClick = () => setShow(!show)

  return (
    <li>
      <figure>
        <div className={layout.section}>
          <img className="rounded-xl w-[350px] shadow relative"
               src={project.img} alt={project.name}
              onClick={onClick} />
          <ShowProject project={project} />
        </div>
      </figure>
    </li>
  )
}

const ShowProject = ({project}) =>{
  return (
    <div className={layout.sectionInfo}>
      <h5 className={`font-poppins font-semibold xs:text-[20px] text-[20px] text-white xs:leading-[76.8px] leading-[66.8px] w-full`}>{project.name}</h5>
      <p className={`${styles.paragraph} max-w-[470px] mt-5`}>{project.desc}</p>
    </div>
  )
}


export default ProjectTable
